<?phpif($token != "" && $account != "" && $mediaPath != "" && $mediaType != "")Setup variables$token = trim($token);$mediaPath = trim($mediaPath);$mediaType = trim($mediaType);$purgeURL = "https://api.edgecast.com/v2/mcc/customers/" . trim($account) . "/ed                                                                                            ge/purge";Create send data$request_params = (object) array('MediaPath' => $mediaPath,'MediaType' => $mediaType);$data = json_encode($request_params);Send the request to Edgecast$ch = curl_init();curl_setopt($ch, CURLOPT_URL, $purgeURL);curl_setopt($ch, CURLOPT_PORT , 443);curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0);curl_setopt($ch, CURLOPT_HEADER, 0);curl_setopt($ch, CURLINFO_HEADER_OUT, 1);curl_setopt($ch, CURLOPT_RETURNTRANSFER, true);curl_setopt($ch, CURLOPT_FORBID_REUSE, 1);curl_setopt($ch, CURLOPT_FRESH_CONNECT, 1);curl_setopt($ch, CURLOPT_CUSTOMREQUEST, "PUT");curl_setopt($ch, CURLOPT_POSTFIELDS,$data);curl_setopt($ch, CURLOPT_HTTPHEADER, array('Authorization: tok:'.$token,'Content-Type: application/json','Accept: application/json','Content-length: '.strlen($data)));$head = curl_exec($ch);$httpCode = curl_getinfo($ch);curl_close($ch);check if errorif ($httpCode['http_code'] != 200){print("Error: <br />" . $head);}else {print("Success.  Cache for path ".$mediaPath." has been purged.");}}else {print("Error: <br />Not all 4 variables were configured.");}?>